import { Component } from "@angular/core";

@Component({
  selector: "home-page",
  templateUrl: "./home-page.component.html",
  styleUrls: ["./home-page.component.css"],
  styles: [
    `sucess: {
      background-color: green
    }`
  ]
})

export class HomePage {
  descricao1 = 'Single way data bind!';

  valor: {
    id: number,
    nome: string,
    descricao: string,
  } = {
    id: 1,
    nome: 'Two ways',
    descricao: 'Databind'
  };

  imagem = 'https://angular.io/assets/images/logos/angular/angular.svg';
  width = 350;

  sucesso = true
}

<h1>Home</h1>
<hr>
<ul>
  <li>{{valor.nome}} - {{valor.descricao}}</li>
  <li>{{valor.id}}</li>
</ul>

<div>
  <div>
    <label>Nome</label>
    <br>
    <input [(ngModel)]="valor.nome" />
  </div>

  <div>
    <label>Descricao</label>
    <br>
    <input [(ngModel)]="valor.descricao">
  </div>

  <div>
    <label>ID</label>
    <br>
    <input [(ngModel)]="valor.id">
  </div>


  <hr>

  <h2>Imagem databinding</h2>
  <img [src]="imagem" [style]="{width: width + 'px'}">
  <div class="alert" [class]="{sucess: sucesso}">
    Alerta
  </div>
