import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { ClientListPage } from './pages/client/client-list-page.component';
import { HomePage } from './pages/home/home-page.component';
import { PaiPageComponent } from './pages/pai/pai-page.component';
import { ModuloFilhoModule } from './modulo-filho.module';

const routes: Routes = [
  { path: 'client', component: ClientListPage },
  { path: '', component: HomePage},
  //Router do component pai, e dos filhos atraves do modulo filho
  { path: 'pai',
    component: PaiPageComponent,
    children: [
      { path: 'child01', loadChildren: () => ModuloFilhoModule },
      { path: 'child02', loadChildren: () => ModuloFilhoModule }
    ]
  },
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],
  exports: [RouterModule]
})
export class AppRoutingModule { }
