import { Component } from "@angular/core";

@Component({
  selector: 'app-client-list',
  templateUrl: './client-list-page.component.html',
  styleUrls: ['./client-list-page.component.css']
})

export class ClientListPage {
  clientePremium = false

  nome = ''

  clientes = [
    {
      nome: 'Cliente 1',
      id: 1
    },
    {
      nome: 'Cliente 2',
      id: 2
    }
  ]

  adicionar() {
    let count = this.clientes.length;
    this.clientes.push({ nome: this.nome, id: count + 1 });
  }
}
<p>Essa é a primeira pagina de clientes</p>

<ul *ngFor="let cliente of clientes">
  <li>{{cliente.nome}} - {{cliente.id}}</li>
</ul>

<label>Nome:</label>
<br>
<input [(ngModel)]="nome">
<br>
<button (click)="adicionar()">Adicionar</button>

<hr>

<div *ngIf="clientePremium">
  <p>Acesso liberado!</p>
</div>

<div *ngIf="!clientePremium">
  Assine o premium para full acesso.
</div>

<button *ngIf="!clientePremium" (click)="clientePremium = true">Assinar</button>
