import { Component } from "@angular/core";
import { ClientService } from "../services/client.service";

@Component({
  selector: 'app-client-list',
  templateUrl: './client-list-page.component.html',
  styleUrls: ['./client-list-page.component.css']
})

export class ClientListPage {
  clientePremium = false

  nome = 'Renan Martins'

  clientes = [
    {
      nome: 'Cliente 1',
      id: 1
    },
    {
      nome: 'Cliente 2',
      id: 2
    }
  ]

  adicionar() {
    let count = this.clientes.length;
    this.clientes.push({ nome: this.nome, id: count + 1 });
  }

  data = new Date()
  moeda = 10.50

  constructor(private clientService: ClientService) {}

  sayHello() {
    alert(this.clientService.sayHello())
  }
}
<p>Essa é a primeira pagina de clientes</p>

<ul *ngFor="let cliente of clientes">
  <li>{{cliente.nome}} - {{cliente.id}}</li>
</ul>

<label>Nome:</label>
<br>
<input [(ngModel)]="nome">
<br>
<button (click)="adicionar()">Adicionar</button>

<!-- <hr> -->

<div>
  <div *ngIf="clientePremium" style="margin: 20px;">
    <p>Acesso liberado!</p>
  </div>

  <div *ngIf="!clientePremium" style="margin: 20px auto;">
    Assine o premium para full acesso.
  </div>

  <button *ngIf="!clientePremium" (click)="clientePremium = true">Assinar</button>
</div>

<hr>

<h2 style="text-align: center;">Exemplo de pipes para formatacao de dados</h2>

<p>Data: {{ data | date: 'long' | uppercase }}</p>
<p>Currency: {{ moeda | currency: 'GBP' }}</p>
<p>Uppercase customizado: {{ nome | customUppercase }}</p>

<hr>

<h3>Diga olá atraves do client service</h3>
<br>
<button (click)="sayHello()">Diga Olá</button>
